	c.loadDictionary = function(query){
		var name = query.trim();
		if (name.length < 4) {
			return [];
		}
		var first = '';
		var last = '';
		var idx = name.indexOf(',');
		if (idx >= 0) {
			last = name.substring(0,idx).trim();
			first = name.substring(idx+1).trim();
		}
		else{
			idx = name.indexOf(' ');
			if(idx >= 0){
				first = name.substring(0,idx).trim();
				last = name.substring(idx+1).trim();
			}
			else{
				first = name.trim();
				last = name.trim();
			}
		}
		var params = {};
		params.first = first;
		params.last = last;
		return SurveyUser.searchByName(params, c.data.sessionId).then(function(rs){
				var tags = [];
				var result = rs.data.result;
				result.forEach(function(r){
					tags.push({text: r.label, id: r.id, name: r.value, org: r.org});
				});
				return tags;
			}, function(error){
				console.error(error);
				return [];
			});
	};

	c.closeMessageBox = function(){
